body.script:
//
  /* --------------------------------------------------------------------------------
    [ --- jelmix Blogger Template --- ]
    Version: 1.0.0
    Author: Jules
    Description: Vanilla ES6+ JavaScript for high-performance interactions.
  -------------------------------------------------------------------------------- */

  document.addEventListener('DOMContentLoaded', () => {

    /* --- 1. Dark Mode --- */
    const darkModeToggle = document.querySelector('.dark-mode-toggle');
    const html = document.documentElement;

    // Apply saved theme on load
    if (localStorage.getItem('theme') === 'dark') {
      html.classList.add('dark-mode');
    }

    if (darkModeToggle) {
      darkModeToggle.addEventListener('click', () => {
        html.classList.toggle('dark-mode');
        if (html.classList.contains('dark-mode')) {
          localStorage.setItem('theme', 'dark');
        } else {
          localStorage.setItem('theme', 'light');
        }
      });
    }

    /* --- 2. Mobile Navigation --- */
    const navToggle = document.querySelector('.mobile-nav-toggle');
    const navMenu = document.querySelector('#header-nav'); // Assuming your nav menu has this ID

    if (navToggle && navMenu) {
      navToggle.addEventListener('click', () => {
        navMenu.classList.toggle('is-active');
        navToggle.classList.toggle('is-active');
      });
    }

    /* --- 2. Lazy Loading for Images & Backgrounds --- */
    const lazyElements = document.querySelectorAll('[data-src], [data-bg]');

    const lazyLoad = (target) => {
      const io = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const el = entry.target;
            const src = el.getAttribute('data-src');
            const bg = el.getAttribute('data-bg');

            if (src) {
              el.setAttribute('src', src);
              el.removeAttribute('data-src');
            }
            if (bg) {
              el.style.backgroundImage = `url(${bg})`;
              el.removeAttribute('data-bg');
            }

            observer.disconnect();
          }
        });
      });
      io.observe(target);
    };
    lazyElements.forEach(lazyLoad);

    /* --- 3. Process Post Body for Lazy Loading --- */
    const postBody = document.querySelector('.post-body');
    if (postBody) {
      const images = postBody.querySelectorAll('img');
      images.forEach(img => {
        const src = img.getAttribute('src');
        img.setAttribute('data-src', src);
        img.removeAttribute('src');
        lazyLoad(img);
      });
    }

  });
//
